mapper.select:
SELECT RNO, BNO, REPLY, REPLIER, REPLYDATE, UPDATEDATE
FROM JUNGMIN_REPLY
WHERE RNO = #{rno}

mapper.update:
UPDATE JUNGMIN_REPLY
SET REPLY = #{reply}, REPLIER = #{replier}, UPDATEDATE = SYSDATE
WHERE RNO = #{rno}

mapper.select:
SELECT RNO, BNO, REPLY, REPLIER, REPLYDATE, UPDATEDATE
FROM JUNGMIN_REPLY
WHERE BNO = #{bno}
